mapper.select:
SELECT COUNT(*) FROM passenger
user_id = #{userId}
AND ( name LIKE CONCAT('%', #{kw}, '%') OR id_no LIKE CONCAT('%', #{kw}, '%') OR phone LIKE CONCAT('%', #{kw}, '%') )

SELECT id, name, gender, id_no, phone
FROM passenger

    user_id = #{userId}

        AND (
        name  LIKE CONCAT('%', #{kw}, '%')
        OR id_no LIKE CONCAT('%', #{kw}, '%')
        OR phone LIKE CONCAT('%', #{kw}, '%')
        )


ORDER BY id DESC
LIMIT #{size} OFFSET #{offset}
